#include "RobotMap.h"
#include "LiveWindow/LiveWindow.h"
// BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=ALLOCATION
SmartCANJaguar* RobotMap::driveTrainRightBack = NULL;
SmartCANJaguar* RobotMap::driveTrainLeftBack = NULL;
SmartCANJaguar* RobotMap::driveTrainRightFront = NULL;
SmartCANJaguar* RobotMap::driveTrainLeftFront = NULL;
SmartGyro* RobotMap::driveTrainGyro = NULL;
Relay* RobotMap::shifterCompressor = NULL;
DigitalInput* RobotMap::shifterPressureSwitch = NULL;
DoubleSolenoid* RobotMap::shifterDoubleSolenoid = NULL;
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=ALLOCATION
void RobotMap::init() {
	// BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS
	LiveWindow* lw = LiveWindow::GetInstance();
	driveTrainRightBack = new SmartCANJaguar(2);
	
	driveTrainLeftBack = new SmartCANJaguar(1);
	driveTrainLeftBack->Invert();
	
	driveTrainRightFront = new SmartCANJaguar(4);

	
	driveTrainLeftFront = new SmartCANJaguar(3);
	driveTrainLeftFront->Invert();
	
	
	driveTrainGyro = new SmartGyro(1, 1);
	lw->AddSensor("Drive Train", "Gyro", driveTrainGyro);
	driveTrainGyro->SetSensitivity(0.007);
	shifterCompressor = new Relay(1, 1);
	lw->AddActuator("Shifter", "Compressor", shifterCompressor);
	
	shifterPressureSwitch = new DigitalInput(1, 1);
	lw->AddSensor("Shifter", "Pressure Switch", shifterPressureSwitch);
	
	shifterDoubleSolenoid = new DoubleSolenoid(1, 1, 2);      
	
	
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS
}
