void Elevator::GoToShootPosition(){
	SetSetpoint(ELEVATOR_SHOOTPOS);
}
void Elevator::GoToMaxPosition(){
	SetSetpoint(ELEVATOR_UP);
}
void Elevator::GoToMinPosition(){
	SetSetpoint(ELEVATOR_DOWN);
}
bool Elevator::NotOKToMove() {
	return elevationEncoder->IsStall();
}
