#include "Launcher.h"
#include "../Robotmap.h"
const int lowPressureLimit = 60;
Launcher::Launcher() : Subsystem("Launcher") {
	// BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
	solenoid = RobotMap::launcherSolenoid;
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
	pressureTransducer = RobotMap::launcherLowPressureSwitch;
}
    
void Launcher::InitDefaultCommand() {
	// BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DEFAULT_COMMAND
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DEFAULT_COMMAND
}
void Launcher::OpenTank() {
	solenoid->Set(true);
}
void Launcher::CloseTank() {
	solenoid->Set(false);
}
//TODO: Constant may need to be calibrated
bool Launcher::IsLowPressure() {
	float PSI = pressureTransducer->GetPressurePSI();
	if (PSI <= lowPressureLimit){
		return true;
	}		
	else 
		return false;
}
